Select tb_customer.nama As nama, tb_customer.alamat As alamat, tb_customer.kota As kota, tb_customer.kodepos As kodepos, tb_customer.no_npwp As no_npwp, tb_invoice.no_invoice As no_invoice, tb_invoice.tgl_invoice As tgl_invoice, tb_invoice.no_order As no_order, tb_invoice.no_referensi As no_referensi, tb_invoice.kegiatan As kegiatan, tb_invoice.no_sertifikat As no_sertifikat, tb_invoice.keterangan As keterangan, tb_invoice.total As total, tb_invoice.ppn As ppn, tb_invoice.total_ppn As total_ppn, tb_invoice.terbilang As terbilang, tb_invoice.terbayar As terbayar, tb_invoice.pasal23 As pasal23, tb_invoice.no_kuitansi As no_kuitansi, tb_fee.barang_id As barang_id, tb_fee.harga As harga, tb_fee.qty As qty, tb_fee.satuan As satuan, tb_fee.jumlah As jumlah, tb_fee.keterangan As keterangan1, tb_barang.nama As nama1, tb_invoice.id From ((tb_invoice Join tb_customer On tb_invoice.customer_id = tb_customer.id) Join tb_fee On tb_invoice.id = tb_fee.invoice_id) Join tb_barang On tb_fee.barang_id = tb_barang.barang_id
