Select tb_invoice.id, tb_invoice.customer_id, tb_invoice.no_invoice, tb_invoice.tgl_invoice, tb_invoice.no_order, tb_invoice.no_referensi, tb_invoice.kegiatan, tb_invoice.no_sertifikat, tb_invoice.keterangan, tb_invoice.total, tb_invoice.ppn, tb_invoice.total_ppn, tb_invoice.terbilang, tb_invoice.terbayar, tb_invoice.pasal23, tb_invoice.no_kuitansi, tb_customer.nama, tb_customer.alamat, tb_customer.kota, tb_customer.kodepos, tb_customer.no_npwp, tb_barang.nama As nama1, tb_pelaksanaan.pelaksanaan_tgl, tb_fee.barang_id, tb_fee.harga, tb_fee.qty, tb_fee.satuan, tb_fee.jumlah, tb_fee.keterangan As keterangan1 From tb_invoice Left Join tb_customer On tb_invoice.customer_id = tb_customer.id Left Join tb_fee On tb_invoice.id = tb_fee.invoice_id Left Join tb_barang On tb_fee.barang_id = tb_barang.barang_id Left Join tb_pelaksanaan On tb_invoice.id = tb_pelaksanaan.invoice_id
